package com.estranger.www.common.enumeration;/** * Author：Estranger * Description：枚举类 * Date：2020/9/11 */public enum ResponseStatusEnum {    /**     * NORMAL_RETURNED 正常返回     * SERVER_ERROR  系统异常     */    NORMAL_RETURNED(1000, "ok"),    PARAMS_ERROR(1003, "Parameters Error"),    FORBIDDEN(1006, "Access Forbidden"),    SERVER_ERROR(1008, "系统异常请稍后再试"),    DATA_NOT_FOUND(1010, "Data Not Found"),    CUSTOMIZE_TOAST(2000, "customize toast");    private Integer status;    private String desc;    ResponseStatusEnum(Integer status, String desc) {        this.status = status;        this.desc = desc;    }    public Integer getStatus() {        return status;    }    public String getDesc() {        return desc;    }}